import React from "react";
import axios from "../";

class Chuck extends React.Component {
    state = {
        categories: []
      }
      componentDidMount() {
        axios.get(`/chuck/categories`)
        .then(response => {
          const categories = response.data;
          this.setState ({categories});
        })
      }
     render() {
      return (
       <ul>
         {this.state.categories.map (post =>  (post.string) )}
       </ul>
      )}
}
export default Chuck
