import React from "react";
import axios from "./../../axios";

class Chuck extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          open: false,
        };
      }
      componentDidMount() {
        axios.get('fact')
        .then(response => {
          const jokes = response.data;
          this.setState ({jokes});

          
        })
        
      }
     
      render() {
            return (
                <div>
                <a onClick={() => this.setState({ open: !this.state.open })}>
                <div>
                  {this.props.food}
                </div>
                </a>
                </div>
            )

            
          }
          
}
export default Chuck

