import React from "react";
import axios from "axios";

class Swapi extends React.Component {
    constructor(props) {
        super(props);
   this.state = {
        array: []
      };
      
   this.renderPeople = this.renderPeople.bind(this);
    }

    componentDidMount(){
        axios
          .get('swapi/people')
          .then(({ data })=> {
            console.log(data);
            this.setState(
              { array: data.name }
            );
          })
          .catch((err)=> {})
      }
     
      render() {
        console.log(this.state.array);
        return(
          <div>
            <h3>Recipes</h3>
            <ul className="list-group">
               {this.renderPeople()}
            </ul>
          </div>
        );
      }
     
      renderPeople() {
        console.log(this.state.array);
        return thisst.map(this.state.array, person => {
          return (
            <li className="list-group-item" key={person.name}>
                {person.name}
            </li>
          );
        });
      }
     }

