import axios from "axios";

class Swapi extends React.Component {
    state = {
        categories: []
      }
      componentDidMount() {
        axios.get('categories')
        .then(response => {
          const categories = response.data;
          this.setState ({categories});
        })
      }
     render() {
      return (
       <ul>
         {this.state.categories.map (post =>  (post.title) )}
       </ul>
      )}
}
